const BASE_URL = "https://git.tyrantg.com/tyrantgenesis/hikerViewRules/raw/master/"
const LOCAL_URL = "hiker://files/rules/TyrantG/"

/* Base Function */
const godWriteFile = (path) => {
  if (Array.isArray(path))
	path.forEach(item => writeFile(LOCAL_URL+item, fetch(BASE_URL+item)))
  else
	writeFile(LOCAL_URL+path, fetch(BASE_URL+path))
}

const godSaveFile = (path) => {
  if (Array.isArray(path))
	path.forEach(item => {
	  if (! fileExist(LOCAL_URL+item)) writeFile(LOCAL_URL+item, fetch(BASE_URL+item))
	})
  else
	if (! fileExist(LOCAL_URL+path)) writeFile(LOCAL_URL+path, fetch(BASE_URL+path))

}
