function lazy(url) {
    return 'x5Rule://' + url + '@' + $.toString(() => {
        var urls = _getUrls()
        for (let i in urls) {
            if (urls[i].match(/\.mp3|\.mp4|\.m3u8|\.m4a/i)) {
                return urls[i] + '&memoryPosition=full#isMusic=true#'
            }
        }
        try {
            document.querySelector(".btn--play").click()
        } catch (e) {}

    })
}
let pic = pd(getResCode(), "img&&srcset")
let arr = pdfa(getResCode(), "body&&.album-list&&.item");
for (let it of arr) {
    let u = pd(it, "a&&href")
    d.push({
        title: pdfh(it, ".title&&Text"),
        url: u.includes("/album/") ? u + ";get;utf_8;{User-Agent@Android}" : lazy(u),
        col_type: "text_center_1",
        desc: pdfh(it, ".info&&Text"),
        pic_url: pic,
        extra: {
            //newWindow: true,
            blockRules: [".mp3", ".m4a"]
        }
    });
}
setResult(d)

