        if (title.length > 20) {
            desc = "↪" + title.substring(20);
            title = title.substring(0,20);
        }

        d.push({
            title: title,
            pic_url: "https://imagev2.xmcdn.com/" + listi.cover,
            desc: desc + "（" + listi.createdAt + "）",
            url: listi.playPath + "#isMusic=true#"
        }); 
    }
}

