js:
var d = [];
var html = getResCode();
var lazy = $('').lazyRule((MY_HOME) => {
    input = input.split('/')
    var html = MY_HOME +
        '/web/index/video_new?code=' +
        input[4] + '&no=' + input[5] +
        '&type=0&timestamp=' +
        new Date().getTime() + '&sign=' +
        md5(
            new Date().getTime() + input[4] +
            input[5] + 'FRDSHFSKVKSKFKS')
    return JSON.parse(request(html)).data.videoUrl +
        ';{Referer@' + MY_HOME + '}'
}, MY_HOME)
d.push({
    title: pdfh(html, '.desc&&.extra&&Text') +
        '\n' + pdfh(html, '.desc&&.extra,1&&Text') +
        '\n' + pdfh(html, '.desc&&.extra,3&&Text'),
    desc: pdfh(html, '.update-time&&Text'),
    img: pd(html, '.book&&img&&src'),
    url: MY_URL,
    col_type: 'movie_1_vertical_pic_blur'
});
var desc = pdfh(html, '.book-intro&&Text');
if (desc.length > 60) {
    desc = desc.replace(desc, desc.substring(0, 60) + "......");
}
d.push({
    title: desc,
    col_type: 'long_text'
});
var list = pdfa(html, '.book-list&&a');

function playLists() {
    d.push({
        title: pdfh(list[j], 'a&&Text'),
        url: pd(list[j], 'a&&href') + lazy,
        col_type: 'text_5'
    });
}
addListener('onClose', $.toString(() => {
    clearMyVar('当前线路名');
    clearMyVar('分集起');
    clearMyVar('分集终');
}))
var 选集数组 = list;
if (选集数组.length > 110) {
    //设置每区选集数目
    var page_number = 100;
    var star = getMyVar('分集起', '1');
    var end = getMyVar('分集终', JSON.stringify(page_number));
    var total = Math.ceil(选集数组.length / page_number);
    var catalogue = []
    for (let i = 0; i < total; i++) {
        catalogue += i * page_number + ',';
        catalogue = catalogue.split(',');
    }
    for (let i = 0; i < 8; i++) {
        d.push({
            col_type: "blank_block"
        })
    }
    for (var i = 0; i < catalogue.length - 1; i++) {
        var total1 = parseInt(catalogue[i]) + 1;
        var total2 = parseInt(catalogue[i + 1]);
        if (i == (catalogue.length - 2)) var total2 = 选集数组.length;
        d.push({
            title: star == total1 ? '‘‘' + total1 + '-' + total2 + '’’' : total1 + '-' + total2,
            url: $("#noLoading#").lazyRule((total1, total2) => {
                putMyVar('分集起', total1);
                putMyVar('分集终', total2);
                refreshPage(false);
                return 'hiker://empty'
            }, total1, total2),
            col_type: 'scroll_button'
        });
    }
    for (var j = star - 1; j < end; j++) {
        playLists()
    }
    d.push({
        col_type: "line"
    })
    var yema = Math.ceil(end / page_number);
    d.push({
        title: "““”” <small><small><font color='grey'>" +
            yema + ' / ' + total +
            "</font> </small></small>",
        col_type: "text_center_1",
        extra: {
            lineVisible: false
        }
    })
}
setResult(d)

