var code = getResCode()
var caches;

function getCache() {
    let c = readFile("chapter.json") || "[]";
    caches = JSON.parse(c)
    let url = getUrl()
    addListener("onRefresh", $.toString((url) => {
        let c = readFile("chapter.json") || "[]";
        c = JSON.parse(c)
        let che = {
            url: url,
            page: 0,
            chapters: []
        };
        for (let i = 0; i < c.length; i++) {
            if (c[i].url == che.url) {
                c[i] = che;
                log("clear:" + url)
                saveFile("chapter.json", JSON.stringify(c))
                break
            }
        }
    }, url))
    for (let it of caches) {
        if (it.url == url) {
            return it;
        }
    }
    return {
        url: url,
        page: 0,
        chapters: []
    }
}

function saveCache(che) {
    log(che.length)
    let c = caches
    if (c.length > cfg.缓存个数) {
        c.shift()
    }
    let exist = false
    for (let i = 0; i < c.length; i++) {
        if (c[i].url == che.url) {
            c[i] = che;
            exist = true;
            break
        }
    }
    if (!exist) {
        c.push(che)
    }
    saveFile("chapter.json", JSON.stringify(c))
}

let cache = getCache();

let mp = pdfa(code, cfg.分页列表)
let page = mp.length;
log("实际页数：" + page)
log("缓存页数：" + cache.page)
if (page == cache.page) {
    //已完结，目录全部缓存了
    setResult(cache.chapters)
    return
}
//解析本地没有的缓存
let urls = []
let htmls = []
//log(mp)
for (let i = cache.page; i < mp.length; i++) {
    if (i == 0) {
        htmls.push(code)
        continue
    }
    let it = mp[i];
    urls.push({
        url: pd(it, cfg.分页链接),
        options: {
            headers: cfg.分页请求头 || {}
        }
    })
}
//log(urls)
let d = [].concat(cache.chapters)
htmls = htmls.concat(batchFetch(urls))
//log(htmls)
for (let it of htmls) {
    if (it == "") {
        break
    }
    let list = pdfa(it, cfg.章节列表)
    let temp = []


var lazy = `@lazyRule=.js:try{var src=parseDom(request(input),'iframe&&src');var jx=request(src,{header:{"User-Agent":PC_UA}});eval(jx.split('<script>')[1].split('function')[0]);eval('var url='+jx.split('mp3:')[1].split('\}')[0]);url.indexOf('http')>-1?url:"toast://获取失败!"}catch(e){input}`;

    for (let j of list) {
        temp.push({
            title: pdfh(j, cfg.章节标题).split(" （")[0].split("（")[0],
            url: pd(j, cfg.章节链接)+lazy ,
extra: {
blockRules: [
                    ".jpg",
                    ".jpeg",
                    ".png",
                    ".mp3",
                    ".m4a",
                    ".ico",
                    ".gif"
                ]
                },
           col_type:'text_2'
        })
    }
    //log(list.length)
    if (list.length == cfg.一页章节数) {
        //章节齐全的才放本地缓存
        cache.page = cache.page + 1
        cache.chapters = cache.chapters.concat(temp)
    }
    d = d.concat(temp)
}

saveCache(cache)
setResult(d)"}]

