var items = [];
var html = getResCode();
//setError(html);
try {
    var list = parseDomForArray(html, '.auto-fit-columns&&.col-auto:has([data-movieid])');

    for (var i = 0; i < list.length; i++) {
        var title = parseDomForHtml(list[i], '.title&&Text');
        var img = parseDom(list[i], '.img-box&&style');
        var play = parseDomForHtml(list[i], '.videocover&&data-movieid');
        var desc = parseDomForHtml(list[i], '.absolute-bottom-right&&Text');
        if (/short/.test(MY_URL)) {
            url = 'https://www.ddder10.xyz/page.php?movieid=' + play + '&ls=2';
        } else {
            url = 'https://www.ddder10.xyz/page.php?movieid=' + play + '&ls=1'
        }
        items.push({
            title: title,
            pic_url: img,
            url: $(url).lazyRule((play) => {
                writeFile('hiker://files/cache/video.m3u8', request(request(input).match(/2":{"id":"(.*?)"/)[1].replace(/\\/g, ''), {
                    headers: {
                        'Referer': input
                    }
                }).replace(/(#EXTINF:.*?,\n)/g, "$1" + 'https://www.ddder001.xyz/slink/movies/' + play + '/'));
                return 'file:///storage/emulated/0/Android/data/com.example.hikerview/files/Documents/cache/video.m3u8#' + input + ';{User-Agent@Windows&&Referer@https://www.ddder001.xyz}'
            }, play),
            desc: desc
        });
    }
} catch (e) {}
setHomeResult({
    data: items
});

